import React, { useEffect } from 'react'
import { useLocation,Link ,useNavigate} from 'react-router-dom';
  
const Navbar = (props) => {
  let location = useLocation();
  useEffect(()=>{
  },[location])

  let navigate = useNavigate();
  const handlelogout=()=>{
    localStorage.removeItem("token");
    props.showalert("loggedout successfully","success")
    navigate("/login");
  }
  return (
    <>
      <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
  <div className="container-fluid">
    <a className="navbar-brand" href="/">iNOTEBOOK</a>
    <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
      <span className="navbar-toggler-icon"></span>
    </button>
    <div className="collapse navbar-collapse" id="navbarSupportedContent">
      <ul className="navbar-nav me-auto mb-2 mb-lg-0">
        <li className="nav-item">
          <a className={`nav-link ${location.pathname==='/'?"active":""}`} aria-current="page" href="/">Home</a>
        </li>
        <li className="nav-item">
          <a className={`nav-link ${location.pathname==='/about'?"active":""}`} href="/about">About</a>
        </li>
      </ul>
      {!localStorage.getItem("token")?<form className="d-flex" role="search">
      <Link className="btn btn-primary mx-1" to="/login" role="button">login</Link>
      <Link className="btn btn-primary mx-1" to="/signup" role="button">signup</Link>
      </form>:<div> <Link className="fa-solid fa-user fa-2xl mx-4 "></Link> <button onClick={handlelogout}className='btn btn-primary'>Logout</button></div>}
    </div>
  </div>
</nav>
    </>
  )
}

export default Navbar
