import NoteContext from "./noteContext";
import { useState } from "react";

const NoteState =(props)=>{
   const  s = {
        "name" : "pankaj",
        "age" : "21"
    }

   const [state,setstate] = useState(s);
   const update =()=>{
    setTimeout(() => {
        setstate({
            "name" : "rahul",
            "age" : "20"
        })

    }, 1000);
}
    return(
        <NoteContext.Provider value={{state,update}}>
            {props.children}
        </NoteContext.Provider>
    )
}

export default NoteState;
