import React, { useContext, useEffect, useRef,useState } from 'react'
import noteContext from '../context/notes/noteContext';
import Addnote from "./Addnote";
import Noteitem from './Noteitem';

const Notes = () => {
  const context = useContext(noteContext);
  const { notes, getnote ,editnote} = context;

  useEffect(() => {
    getnote();
  }, [])
  const ref = useRef(null);
  const [note,setNote] = useState({id:"",etitle:"",edescription:"",etag:"default"})

    const updatenote = (currentnote) => {
      ref.current.click();
      setNote({id: currentnote._id,})

    }

  const handleclick = (e)=>{
    e.preventDefault();
    editnote(note._id,note.etitle,note.edescription,note.etag)

}

const onchange = (e)=>{
setNote({...note ,[e.target.name]: e.target.value} )
} 
  return (
    <div>
      <div className="row my-3">
        <Addnote />
        <button type="button" ref={ref} className="btn btn-primary d-none" data-bs-toggle="modal" data-bs-target="#exampleModal">
          Launch demo modal
        </button>
        <div className="modal fade" id="exampleModal" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title" id="exampleModalLabel">Add note</h5>
                <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
              </div>
              <div className="modal-body">
                <form className='my-3'>
                  <div className="mb-3">
                    <label htmlFor="title" className="form-label">Title</label>
                    <input type="text" className="form-control" id="etitle" name="etitle" aria-describedby="emailHelp" onChange={onchange} />
                  </div>
                  <div className="mb-3">
                    <label htmlFor="description" className="form-label">Description</label>
                    <input type="text" className="form-control" id="edescription" name="edescription" onChange={onchange} />
                  </div>
                  <div className="mb-3">
                    <label htmlFor="tag" className="form-label">Tag</label>
                    <input type="text" className="form-control" id="etag" name="etag" onChange={onchange} />
                  </div>
                </form>
              </div>
              <div className="modal-footer">
                <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                <button type="button" className="btn btn-primary" onClick={handleclick}>Upadte Note</button>
              </div>
            </div>
          </div>
        </div>

        <h2>Your Notes</h2>
        {notes.map((note) => {
          return <Noteitem key={note._id} note={note} updatenote={updatenote} />;
        })}
      </div>
    </div>
  )
}

export default Notes
