import NoteContext from "./noteContext";
import { useState } from "react";

const NoteState =(props)=>{
  const Host = "http://localhost:5000";
    const noteinitial =[]
    const[notes,setnotes] = useState(noteinitial);

     // Get all notes

    const getnote= async()=>{
      // API call
      const response = await fetch(`${Host}/api/notes/fetchAllUser`,{
        method: 'GET',
        headers:{
          "Content-Type": "application/json",
          "auth-token": "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJVc2VyIjp7ImlkIjoiNjQ0ZDYwZjg1ZWIxYzIwNDM1MGQ1ZDg3In0sImlhdCI6MTY4Mjc5NTE3NX0.v2Ee415ap7dzqVGjk8vQgfb5nBz_SvdsrNOjxJkF5X0"
        }
      });

    }
    // Add a note
    const addnote= async(title,description,tag)=>{
      console.log("adding a new note");
      // API call
      const response = await fetch(`${Host}/api/notes/addnote`,{
        method: 'POST',
        headers:{
          "Content-Type": "application/json",
          "auth-token": "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJVc2VyIjp7ImlkIjoiNjQ0ZDYwZjg1ZWIxYzIwNDM1MGQ1ZDg3In0sImlhdCI6MTY4Mjc5NTE3NX0.v2Ee415ap7dzqVGjk8vQgfb5nBz_SvdsrNOjxJkF5X0"
        },
        body: JSON.stringify(title,description,tag)
      });

      //Logic to add a note
      console.log("adding a new note");
      const note = {
        "_id": "64590abe00ddfcd0b2500f234",
        "user": "644d60f85eb1c204350df5d87",
        "title": title,
        "description": description,
        "tag": tag,
        "date": "2023-05-08T14:44:14.359Z",
        "__v": 0
      }
      setnotes(notes.concat(note))
    }

    // Delete a note
    const deleteNote=(id)=>{
      const newnote = notes.filter((note)=>{return note._id!==id})
      setnotes(newnote);
    }

    // Edit a note
    const editNote= async(id,title,description,tag)=>{
      // API call
      const response = await fetch(`${Host}/api/notes/updatenote/${id}`,{
        method: "POST",
        headers:{
          "Content-Type": "application/json",
          "auth-token": "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJVc2VyIjp7ImlkIjoiNjQ0ZDYwZjg1ZWIxYzIwNDM1MGQ1ZDg3In0sImlhdCI6MTY4Mjc5NTE3NX0.v2Ee415ap7dzqVGjk8vQgfb5nBz_SvdsrNOjxJkF5X0"
        },
        body: JSON.stringify(title,description,tag)
      });
      const json = response.json();

      //Logic to edit in client
        for(let i=0;i<notes.length;i++){
            const element = notes[i];
            if(element._id===id){
              element.title = title;
              element.description = description;
              element.tag = tag;
            }
        }
    }


    return(
        <NoteContext.Provider value={{notes,addnote,deleteNote,editNote}}>
            {props.children}
        </NoteContext.Provider>
    )
}

export default NoteState
