    // }
  return (
    <div style={{height:"50px"}}>
     { props.alert && <div className="alert alert-success alert-dismissible fade show"role="alert">
           <strong> {capitalize(props.alert.type)}</strong> : {(props.alert.msg)} 
      </div>}
    </div>
  )
}
